function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}
var displayClock;
var breakTime = $("#breakTime").text();
var sessionTime = $("#sessionTime").text();

$("#plusBreak").click(function () {
    breakTime = +breakTime + 1;
    $("#breakTime").text(breakTime);
});
$("#minusBreak").click(function () {
    (breakTime > 1) ? breakTime = +breakTime - 1: breakTime = 1;
    $("#breakTime").text(breakTime);
});

$("#plusSession").click(function () {
    sessionTime = +sessionTime + 1;
    $("#sessionTime").text(sessionTime);
});
$("#minusSession").click(function () {
    (sessionTime > 1) ? sessionTime = +sessionTime - 1: sessionTime = 1;
    $("#sessionTime").text(sessionTime);
});
var starts = 0;
var state = true;
var state2 = true;

function showTime(minutes) {
    var counter = 1;
    var totalSeconds = minutes * 60;
    displayClock = setInterval(function () {
        
        var seconds = totalSeconds;
        var display = (seconds - counter) % 60;
        minutes = minutes.toString();
        minutes = minutes.replace(/\.\d{1,}/g, "");
        if (minutes < 1) minutes = 0;
        if (display===59 && minutes) minutes -= 1;
        $(".minutes").text(minutes);
        $(".seconds").text(display);
        counter++;
        if (totalSeconds == counter) {
            clearInterval(displayClock);
            totalSeconds = 0;
            counter = 0;
            changeState();
        }
    }, 1000);

}

function changeState() {
    beep();
    if ($("#session").text() === "SESSION") {
        displayClock = null;
        $("#session").text("BREAK");
        $("#clockdiv").css("border-color", "red");
        clearInterval(displayClock);
        console.log("break started");
        showTime(breakTime);
    } else if (($("#session").text() === "BREAK")) {
        displayClock = null;
        $("#session").text("SESSION");
        $("#clockdiv").css("border-color", "green");
        clearInterval(displayClock);
        showTime(sessionTime);
        console.log("session started");
    }
}


$("#startTimer").on("click", function () {
    if (state2 && state) {
        $("#session").text("SESSION");
        $("#clockdiv").css("border-color", "green");
        starts++;
        //clearInterval(displayClock);
        showTime(sessionTime);
        state2 = false;
    }
});


$(".pause").click(function () {
    if (state && !state2) {
        clearInterval(displayClock);
        state = false;
    } else if (!state && !state2) {
            var pausedSeconds = $(".seconds").text() / 60;
            var pausedMinutes = $(".minutes").text();
            var total = +pausedMinutes + pausedSeconds;
            console.log(total);
            clearInterval(displayClock);
            showTime(total);
            state = true;
    }
});

$(".stop").click(function () {
    if (!state2) {
        state2 = true;
        state = true;
        console.log("state 1: " + state + " state2: " + state2);
        clearInterval(displayClock);
        $(".minutes").text(0);
        $(".seconds").text(0);
    }
});
